#include<iostream>
#include<string>
#include<cmath>
using namespace std;

//将A增加至大于B，然后判断B是否是A的子串，如果不是，则再增加一个A再判断还不是的话就返回-1
int repeatedStringMatch(string A, string B)
{
    if(B.size() == 0)
        return -1;
    int size1 = A.size();
    int size2 = B.size();
    int count = max(1, int(ceil(size2/float(size1))));
    string A_ = "";
    for(int i = 0; i < count; i++)
        A_ += A;
    if(A_.find(B) == string::npos)
    {
        A_ += A;
        count++;
        if(A_.find(B) == string::npos)
            return -1;
        return count;
    }
    return count;
}

int main()
{
    string A = "a";
    string B = "";
    int res = repeatedStringMatch(A, B);
    cout<<res<<endl;
    system("pause");
    return 0;
}